import{_ as s,c as n,o as a,a as l}from"./app.a721e852.js";const h=JSON.parse('{"title":"husky","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u9879\u76EE\u63A5\u5165","slug":"\u9879\u76EE\u63A5\u5165"},{"level":2,"title":"\u6269\u5C55","slug":"\u6269\u5C55"}],"relativePath":"rules/husky/readme.md","lastUpdated":1654076035000}'),p={name:"rules/husky/readme.md"},o=l(`<h1 id="husky" tabindex="-1">husky <a class="header-anchor" href="#husky" aria-hidden="true">#</a></h1><blockquote><p>Modern native git hooks made easy</p></blockquote><h2 id="\u9879\u76EE\u63A5\u5165" tabindex="-1">\u9879\u76EE\u63A5\u5165 <a class="header-anchor" href="#\u9879\u76EE\u63A5\u5165" aria-hidden="true">#</a></h2><p>usage</p><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#676E95;font-style:italic;"># \u81EA\u52A8\u5B89\u88C5\uFF08\u63A8\u8350\uFF09</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># https://typicode.github.io/husky/#/?id=automatic-recommended</span></span>
<span class="line"><span style="color:#A6ACCD;">npx husky-init </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> npm install       </span><span style="color:#676E95;font-style:italic;"># npm</span></span>
<span class="line"><span style="color:#A6ACCD;">npx husky-init </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> yarn              </span><span style="color:#676E95;font-style:italic;"># Yarn 1</span></span>
<span class="line"><span style="color:#A6ACCD;">yarn dlx husky-init --yarn2 </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> yarn </span><span style="color:#676E95;font-style:italic;"># Yarn 2+</span></span>
<span class="line"><span style="color:#A6ACCD;">pnpm dlx husky-init </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> pnpm install </span><span style="color:#676E95;font-style:italic;"># pnpm</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">\u6216\u4F7F\u7528</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">npx auto-husky</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">\u6216\u624B\u52A8\u64CD\u4F5C</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">npm i -D husky</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># \u624B\u52A8\u542F\u7528 Git \u6302\u94A9</span></span>
<span class="line"><span style="color:#A6ACCD;">npm set-script prepare </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">husky install</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">npm run prepare</span></span>
<span class="line"></span></code></pre></div><p>config</p><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#676E95;font-style:italic;"># Add a hook:</span></span>
<span class="line"><span style="color:#A6ACCD;">npx husky add .husky/pre-commit </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">npx --no -- lint-staged</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># \u6216</span></span>
<span class="line"><span style="color:#A6ACCD;">npx husky add .husky/pre-commit </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">npm run lint-staged</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">npx husky add .husky/pre-commit </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">npm test</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">npx husky add .husky/commit-msg </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">npx --no -- commitlint --edit &quot;\${1}&quot;</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;font-style:italic;"># \u8FD9\u4E2A\u6267\u884C\u6709\u95EE\u9898</span></span>
<span class="line"><span style="color:#A6ACCD;">yarn husky add .husky/commit-msg </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">npx --no -- commitlint --edit &quot;\${1}&quot;</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;font-style:italic;"># \u8FD9\u4E2A\u53EF\u4EE5</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># husky uninstall</span></span>
<span class="line"><span style="color:#A6ACCD;">npm uninstall husky </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> git config --unset core.hooksPath</span></span>
<span class="line"></span></code></pre></div><p>\u6CE8\u610F: \u9488\u5BF9\u5BA2\u6237\u7AEF git hooks \u800C\u8A00\uFF0C\u5F88\u5BB9\u6613\u901A\u8FC7 <code>git commit --no-verify</code> \u800C\u8DF3\u8FC7\u68C0\u67E5\uFF0C\u6240\u4EE5\u5BF9\u5E94\u89C4\u8303\u9700\u8981\u5728 CI \u6D41\u7A0B\u4E2D\u5361\u70B9\u3002</p><h2 id="\u6269\u5C55" tabindex="-1">\u6269\u5C55 <a class="header-anchor" href="#\u6269\u5C55" aria-hidden="true">#</a></h2><p>git \u5141\u8BB8\u5728\u5404\u79CD\u64CD\u4F5C\u4E4B\u524D\u6DFB\u52A0\u4E00\u4E9B hook \u811A\u672C\uFF0C\u5982\u672A\u6B63\u5E38\u8FD0\u884C\u5219 git \u64CD\u4F5C\u4E0D\u901A\u8FC7\u3002</p><ul><li>Husky Hooks <ol><li>pre-commit</li><li>pre-push</li><li>post-merge</li><li>pre-receive</li><li>prepare-commmit-msg</li></ol></li><li>Task Runner <ol><li>Lint commit-msg</li><li>Analyze code</li><li>Format code</li><li>Run tests</li><li>Run scripts</li></ol></li></ul><p>git hooks \u53EF\u4F7F\u7528 <code>core.hooksPath</code> \u81EA\u5B9A\u4E49\u811A\u672C\u4F4D\u7F6E\u3002</p><p><strong>\u4E3A\u4EC0\u4E48\u7528 husky\uFF1F</strong></p><p>\u6211\u4EEC\u53EA\u4F1A\u7528\u5230\u201C\u63D0\u4EA4\u5DE5\u4F5C\u6D41\u201D\u94A9\u5B50\uFF0C\u63D0\u4EA4\u5DE5\u4F5C\u6D41\u5305\u542B 4 \u4E2A\u94A9\u5B50\uFF1A</p><ul><li><code>pre-commit</code> \u5728\u63D0\u4EA4\u4FE1\u606F<strong>\u7F16\u8F91\u524D</strong>\u8FD0\u884C\uFF0C\u5728\u8FD9\u4E2A\u9636\u6BB5\u585E\u5165<strong>\u4EE3\u7801\u68C0\u67E5</strong>\u6D41\u7A0B\uFF0C\u68C0\u67E5\u672A\u901A\u8FC7\u8FD4\u56DE\u975E\u96F6\u503C\u5373\u53EF\u505C\u6B62\u63D0\u4EA4\u6D41\u7A0B\uFF1B</li><li><code>prepare-commit-msg</code> \u5728\u9ED8\u8BA4\u4FE1\u606F\u88AB\u521B\u5EFA\u4E4B\u540E\u8FD0\u884C\uFF0C\u6B64\u65F6\u6B63\u662F<strong>\u542F\u52A8\u7F16\u8F91\u5668\u524D</strong>\uFF0C\u53EF\u5728\u8FD9\u4E2A\u9636\u6BB5\u52A0\u8F7D <code>commitizen</code> \u4E4B\u7C7B\u7684\u8F85\u52A9\u586B\u5199\u5DE5\u5177\uFF1B</li><li><code>commit-msg</code> \u5728<strong>\u5B8C\u6210\u7F16\u8F91\u540E</strong>\u8FD0\u884C\uFF0C\u53EF\u5728\u8FD9\u4E2A\u9636\u6BB5\u501F\u52A9 <code>commitlint</code> \u8FDB\u884C\u63D0\u4EA4\u4FE1\u606F\u89C4\u8303\u6027\u68C0\u67E5\uFF1B</li><li><code>post-commit</code> \u5728<strong>\u63D0\u4EA4\u5B8C\u6210\u540E</strong>\u8FD0\u884C\uFF0C\u5728\u8FD9\u4E2A\u9636\u6BB5\u4E00\u822C\u505A\u4E00\u4E9B\u901A\u77E5\u64CD\u4F5C\u3002</li></ul><p>\u4F7F\u7528 Git \u94A9\u5B50\u6700\u76F4\u89C2\u7684\u65B9\u5F0F\u662F\u64CD\u4F5C <code>.git/hooks</code> \u4E0B\u7684\u793A\u4F8B\u6587\u4EF6\uFF0C\u5C06\u5BF9\u5E94\u94A9\u5B50\u6587\u4EF6\u7684 <code>.sample</code> \u540E\u7F00\u540D\u79FB\u9664\u5373\u53EF\u542F\u7528\u3002\u7136\u800C\u8FD9\u79CD\u64CD\u4F5C\u65B9\u5F0F\u5B58\u5728\u5F0A\u7AEF\uFF1A</p><ul><li>\u9700\u8981\u64CD\u4F5C\u9879\u76EE\u8303\u56F4\u5916\u7684 <code>.git</code> \u76EE\u5F55</li><li>\u65E0\u6CD5\u540C\u6B65 <code>.git/hooks</code> \u5230\u8FDC\u7A0B\u4ED3\u5E93</li></ul><p>\u4E24\u4E2A\u5F0A\u7AEF\u53EF\u4EE5\u901A\u8FC7\u4E3A Git \u6307\u5B9A hooks \u76EE\u5F55\u5B8C\u7F8E\u907F\u8FC7\uFF0CHusky \u4FBF\u662F\u57FA\u4E8E\u6B64\u65B9\u6848\u5B9E\u73B0</p><div class="language-bash"><span class="copy"></span><pre><code><span class="line"><span style="color:#676E95;font-style:italic;"># husky \u5373\u901A\u8FC7\u81EA\u5B9A\u4E49 core.hooksPath \u5E76\u5C06 npm scripts \u5199\u5165\u5176\u4E2D\u7684\u65B9\u5F0F\u6765\u5B9E\u73B0\u6B64\u529F\u80FD\u3002</span></span>
<span class="line"><span style="color:#A6ACCD;">git config </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">core.hooksPath</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;"> .husky</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">npm set-script prepare </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">husky install</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"></span></code></pre></div><p>\u6CE8: husky@4 \u8001\u7248\u672C\u914D\u7F6E\u65B9\u5F0F\u662F\u4E0D\u4E00\u6837\u7684\uFF0C\u5982\u4E0B</p><div class="language-json"><span class="copy"></span><pre><code><span class="line"><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">husky</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">: </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">hooks</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#89DDFF;">&quot;</span><span style="color:#FFCB6B;">pre-commit</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">pretty-quick --staged</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;">}</span></span>
<span class="line"><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;">,</span></span>
<span class="line"></span></code></pre></div><p>\u6CE8: <code>--no-install</code> \u9009\u9879\u5DF2\u5F03\u7528\uFF0C\u5E76\u5C06\u8F6C\u6362\u4E3A <code>--no</code></p><p>\u5728 CI/Docker/Prod \u4E2D\u7981\u7528 <code>husky</code>?</p><p>\u53C2\u89C1<a href="https://typicode.github.io/husky/#/?id=disable-husky-in-cidockerprod" target="_blank" rel="noopener noreferrer">\u5B98\u65B9\u6587\u6863</a></p>`,24),e=[o];function t(c,i,r,y,D,d){return a(),n("div",null,e)}var m=s(p,[["render",t]]);export{h as __pageData,m as default};
